select `trans`.`id` AS `id`,`users`.`id` AS `personid`,concat(`users`.`name`,' ',`users`.`famili`) AS `person`,`trans`.`transdate` AS `transdate`,(case when (`trans`.`creditor` = 0) then concat('-',`trans`.`debtor`) else `trans`.`creditor` end) AS `currency`,`trans`.`description` AS `description` from (`taa_transaction` `trans` join `taa_users` `users`) where (`users`.`id` = `trans`.`userid`)